import React, { useState } from 'react';

function InputSample() {
  const [inputs, setInputs] = useState({
    id: '',
    pw: ''
  });

  const { id, pw } = inputs; // 비구조화 할당을 통해 값 추출

  const onChange = (e) => {
    const { value, id } = e.target; // 우선 e.target 에서 name 과 value 를 추출
    setInputs({
      ...inputs, // 기존의 input 객체를 복사한 뒤
      [id]: value // name 키를 가진 값을 value 로 설정
    });
  };

  const onReset = () => {
    setInputs({
      id: '',
      pw: '',
    })
  };


  return (
    <div>
      <input name="id" placeholder="아이디" onChange={onChange} value={id} />
      <input name="pw" placeholder="비밀번호" onChange={onChange} value={pw}/>
      <button onClick={onReset}>초기화</button>
      <div>
        <b>값: </b>
        {id} ({pw})
      </div>
    </div>
  );
}

export default InputSample;
