import React, { useState } from 'react';

function InputSample() {

  const [ inputs, setInputs ] = useState({
    id: '',
    pw: ''
  });

  const { id, pw } = inputs;

  const onChange = (e) => {
    const { value, id } = e.target;
    setInputs({
      ...inputs,
      [id]: value
    });
  };

  const onReset = () => {
    setInputs({
      id: '',
      pw: '',
    })
  };


  return (
    <>
      <input name='id' placeholder="id를 입력해주세요" onChange={onChange} value={id} />
      <input name='pw'placeholder="비밀번호를 입력해 주세요" onChange={onChange} value={pw} />
      <button onClick={onReset}>초기화</button>
      <div>
        <b>값: </b>
        {id} ({pw})
      </div>
    </>
  );
}

export default InputSample;
