import React, {useState} from 'react';

function InputSample() {
    const [inputs, setInputs] = useState({
        id:'',
        pw:''
    });

    const { id,pw } = inputs;

    const onChange= e => {
        const { value, name } = e.target;
        setInputs({
            ...inputs,
            [id]: value
        })
    };

    const onReset = () => {
        setInputs({
            id: '',
            pw: '',
        })
    };

    return (
        <>
            <input name='id' placeholder='id를 입력해주세요' onChange={onchange} value={id}/>
            <input name='pw' placeholder='pw를 입력해주세요' onChange={onchange} value={pw}/>
            <button onClick={onReset}>초기화</button>
            <div>
                <b>값: </b>
                id (pw)
            </div>
        </>
    )
}

export default InputSample;
